import React from "react";
import {useState, useEffect} from "react"
import {PhotoItem} from "../PhotoItem/photoItem";

export const PhotoList = () => {
  const [photos, setPhotos] = useState([])
  const [startIndex, setStartIndex] = useState(0)
  const [lastIndex, setLastIndex] = useState(4)

  const getPhotos = async () => {
    await fetch('https://jsonplaceholder.typicode.com/photos')
      .then(response => response.json())
      .then(json => setPhotos(json))
  }

  useEffect(() => {
    getPhotos()
  }, [])

  const showMoreElements = () => {
    setLastIndex((prev) => prev+4)
  }

  return <div style={{background: "lightblue"}}>
    <div>Photo List
    {photos.slice(startIndex, lastIndex).map((item: any) => {
      return <PhotoItem item={item} key={item.id} />
    })}
    </div>
    <button onClick={() => showMoreElements()} style={{width: '120px', height: '40px'}}>Show more</button>
  </div>
}
